import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

/* Component */
export default function MyD3Component({ data }) {
  const d3Container = useRef(null);
  useEffect(() => {
    if (data && d3Container.current) {
      const svg = d3.select(d3Container.current);

      // Bind D3 data
      const update = svg
        .append("g")
        .selectAll("text")
        .data(data);
    }
  }, [data, d3Container.current]);

  return (
    <div className="chart-example" width={400} height={200} ref={d3Container} />
  );
}
