import React, { useRef, useEffect } from "react";
import bubbleChart from "./bubbleChart";
import * as d3 from "d3";
import sampleData from "./sampleData.csv";

/* Component */
export default function MyD3Component({ data }) {
  const d3Container = useRef(null);
  useEffect(() => {
    if (data && d3Container.current) {
      const svg = d3.select(d3Container.current);
      // .datum()

      // Bind D3 data
      var chart = bubbleChart("bubble")
        .width(600)
        .height(400);
      const csvData = d3.csvParse(sampleData);
      svg.datum(csvData).call(chart);
    }
  }, [data, d3Container.current]);

  return (
    <div className="chart-example" width={400} height={200} ref={d3Container} />
  );
}
